INSERT INTO `field_revision_body` VALUES ('node','faq_item',0,36,36,'und',0,'FAQs (or Frequently Asked Questions) display a list of common questions for your site visitors. Clicking any question reveals the answer. FAQs allow site visitors to quickly find answers to their questions without consuming your time. FAQs are a custom content type created by the FAQ feature.</p><p>To add FAQs, click Add content > FAQ.</p><p><a href=\"http://www.drupalgardens.com/learnmore/samplefaq\" target=\"_blank\">Learn more</a>','','safe_html');
